if (typeof window === 'undefined') {
    self.addEventListener("install", () => self.skipWaiting());
    self.addEventListener("activate", (event) => event.waitUntil(self.clients.claim()));

    self.addEventListener("fetch", (event) => {
        const { request } = event;
        if (request.cache === "only-if-cached" && request.mode !== "same-origin") return;

        // Skip non-GET/POST and external analytics
        if (request.url.includes("cdn-cgi") || request.url.includes("analytics") || request.url.includes("rum")) {
            return;
        }

        event.respondWith(
            fetch(request).then((response) => {
                if (response.status === 0) return response;

                // Statuses that cannot have a body (204 No Content, 205, 304 Not Modified)
                const bodylessStatuses = [204, 205, 304];
                if (bodylessStatuses.includes(response.status)) {
                    return response;
                }

                const newHeaders = new Headers(response.headers);
                newHeaders.set("Cross-Origin-Embedder-Policy", "require-corp");
                newHeaders.set("Cross-Origin-Resource-Policy", "cross-origin");
                newHeaders.set("Cross-Origin-Opener-Policy", "same-origin");

                return new Response(response.body, {
                    status: response.status,
                    statusText: response.statusText,
                    headers: newHeaders,
                });
            }).catch(e => {
                console.error("COI Service Worker Error:", e);
                // Return original response if possible on error
                return fetch(request);
            })
        );
    });
} else {
    // Registration logic
    (async () => {
        if ("serviceWorker" in navigator) {
            const registration = await navigator.serviceWorker.register(window.document.currentScript.src);
            
            registration.addEventListener("updatefound", () => {
                window.location.reload();
            });

            if (registration.active && !navigator.serviceWorker.controller) {
                window.location.reload();
            }
        }
    })();
}
